function filterBrand(brand) {
    window.location.href = `products.html?brand=${brand}`;
}

function loadProducts() {
    const urlParams = new URLSearchParams(window.location.search);
    const brand = urlParams.get('brand');
    const container = document.getElementById('products-container');
    
    if (!container) return;

    const brandProducts = products[brand] || [];
    document.getElementById('brand-title').innerText = `منتجات ${brand ? brand.toUpperCase() : 'الجوالات'}`;

    container.innerHTML = brandProducts.map(p => `
        <div class="product-card" onclick="location.href='details.html?id=${p.id}&brand=${brand}'">
            <img src="${p.image}" alt="${p.name}">
            <div class="product-info">
                <h3>${p.name}</h3>
                <p class="model">${p.model}</p>
                <p class="price">${p.price}</p>
                <button class="buy-btn">عرض التفاصيل</button>
            </div>
        </div>
    `).join('');
}

function loadDetails() {
    const urlParams = new URLSearchParams(window.location.search);
    const id = urlParams.get('id');
    const brand = urlParams.get('brand');
    const container = document.getElementById('details-content');

    if (!container) return;

    const product = products[brand].find(p => p.id === id);
    
    container.innerHTML = `
        <div class="details-container">
            <div class="details-image">
                <img src="${product.image}" alt="${product.name}">
            </div>
            <div class="details-info">
                <h2>${product.name}</h2>
                <table class="specs-table">
                    <tr><td>الموديل:</td><td>${product.model}</td></tr>
                    <tr><td>المواصفات:</td><td>${product.specs}</td></tr>
                    <tr><td>السعر:</td><td class="price">${product.price}</td></tr>
                </table>
                <button class="buy-btn" onclick="showPurchaseMessage()">شراء الآن</button>
            </div>
        </div>
    `;
}

function showPurchaseMessage() {
    const telegramLink = "https://t.me/+967776261314";
    const message = "لأكثر أماناً لشرائك من منتجاتنا، سوف يتم الشراء عبر تلجرام بالتواصل مع أحد موظفينا. للمواصلة اضغط موافق للانتقال للشراء.";
    
    if (confirm(message)) {
        window.location.href = telegramLink;
    }
}

// Initialize based on page
window.onload = () => {
    if (window.location.pathname.includes('products.html')) loadProducts();
    if (window.location.pathname.includes('details.html')) loadDetails();
};
